<?php

namespace Orm\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * WebsiteSettingsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class WebsiteSettingsRepository extends EntityRepository
{
  /**
   * deletes all website settings for the given website id
   *
   * @param string $websiteId
   *
   * @return  array
   */
  public function deleteByWebsiteId($websiteId)
  {
    $dql = 'DELETE FROM Orm\\Entity\\WebsiteSettings ws WHERE ws.websiteid = :websiteid';

    $query = $this->_em->createQuery($dql);
    $query->setParameter('websiteid', $websiteId);
    return $query->getResult();
  }
}
